package functional_ramming;

import java.util.concurrent.TimeUnit;

public class App {

    public static void main(String[] args) throws Exception {
        GameFrame g = new GameFrame();

        while (g != null) {
            g.writeSize();
            g.c.step();
            g.repaint();
            //TimeUnit.MILLISECONDS.sleep(1000 / 60);
        }

    }
}
